var Client   = require('../src').Client;
var clearbit = new Client;

clearbit.PersonCompany.setVersion('2015-05-30');

clearbit.PersonCompany.find({email: 'lachy@stripe.com', webhook_url: 'http://requestb.in/oms5esom'})
  .then(function (response) {
    console.log(response);
  })
